 DROP TABLE "NEOIB_SI"."IMSI_USR_DAT";

CREATE SEQUENCE IMSI_USR_SEQ
       INCREMENT BY 1
       START WITH 1
       MINVALUE 1
       MAXVALUE 999999
       NOCYCLE
       NOCACHE
       NOORDER;
     
 CREATE TABLE "NEOIB_SI"."IMSI_USR_DAT" (	
 	"IDX" NUMBER(10,0) NOT NULL ENABLE, 
	"USR_ID" VARCHAR2(50), 
	"USR_NM" VARCHAR2(50), 
	"CNTRY" VARCHAR2(50), 
	"PHONE" VARCHAR2(50), 
	"COLOR" VARCHAR2(50), 
	"PRICE" NUMBER(11,2),
	"QUAN" NUMBER(11),
	"DT" DATE, 
	"CDT" DATE DEFAULT SYSDATE NOT NULL ENABLE, 
	"UDT" DATE, 
	 CONSTRAINT "IMSI_USR_DAT_PK" PRIMARY KEY ("IDX")
);

SELECT 	  ROWNUM AS IDX
		, USR_ID
		, USR_NM
		, CNTRY
		, PHONE
		, COLOR
		, PRICE
		, QUAN
		, TO_CHAR(DT, 'YYYY-MM-DD') AS DT
FROM IMSI_USR_DAT
ORDER BY DT;





COMMIT;





